/// Get powers from base.
pub(crate) fn get_powers(radix: u32)
    -> &'static ModeratePathPowers<u64>
{
    debug_assert_radix!(radix);

    #[cfg(not(feature = "radix"))] {
        &BASE10_POWERS
    }

    #[cfg(feature = "radix")] {
        match radix {
            3  => &BASE3_POWERS,
            5  => &BASE5_POWERS,
            6  => &BASE6_POWERS,
            7  => &BASE7_POWERS,
            9  => &BASE9_POWERS,
            10 => &BASE10_POWERS,
            11 => &BASE11_POWERS,
            12 => &BASE12_POWERS,
            13 => &BASE13_POWERS,
            14 => &BASE14_POWERS,
            15 => &BASE15_POWERS,
            17 => &BASE17_POWERS,
            18 => &BASE18_POWERS,
            19 => &BASE19_POWERS,
            20 => &BASE20_POWERS,
            21 => &BASE21_POWERS,
            22 => &BASE22_POWERS,
            23 => &BASE23_POWERS,
            24 => &BASE24_POWERS,
            25 => &BASE25_POWERS,
            26 => &BASE26_POWERS,
            27 => &BASE27_POWERS,
            28 => &BASE28_POWERS,
            29 => &BASE29_POWERS,
            30 => &BASE30_POWERS,
            31 => &BASE31_POWERS,
            33 => &BASE33_POWERS,
            34 => &BASE34_POWERS,
            35 => &BASE35_POWERS,
            36 => &BASE36_POWERS,
            // Powers of 2, and others, should already be handled by now.
            _  => unreachable!(),
        }
    }
}

// TESTS
// -----

#[cfg(test)]
mod tests {
    use crate::util::test::*;
    use super::*;

    #[test]
    fn normalization_test() {
        // Ensure each valid is normalized.
        for base in BASE_POWN.iter().cloned() {
            let powers = get_powers(base);
            for idx in 0..powers.small.len() {
                let fp = powers.get_small(idx);
                assert_eq!(fp.mant.leading_zeros(), 0);
            }
            for idx in 0..powers.large.len() {
                let fp = powers.get_large(idx);
                assert_eq!(fp.mant.leading_zeros(), 0);
            }
        }
    }

    #[cfg(feature = "radix")]
    #[test]
    #[should_panic]
    fn pow2_test() {
        for base in BASE_POW2.iter().cloned() {
            let _ = get_powers(base);
        }
    }
}
